import React, {useState} from 'react';
import InvestmentChart from '../../models/InvestmentChart';
import { stockData, availableDates, firstDate } from '../../models/RealStockData';
import { InputAmount, InputDate, Result, Button } from './Inputs';
import { useGuessModel } from '../../models/model';

function Chart() {
  const [startDate, setStartDate] = useState<string>(firstDate);
  const [initialInvestment, setInitialInvestment] = useState<number>(100);
  const [finalValue, setFinalValue] = useState<number>(100);

  return (
    <div className='mt-4'>
      <h2>Investment Return Over Time</h2>
      <InvestmentChart 
        startDate={startDate} 
        initialInvestment={initialInvestment} 
        finalValue={finalValue} 
      />
    </div>
  );
}

export default Chart;
