import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables);



const InvestmentChart: React.FC = () => {
  const [inputAmount, setInputAmount] = useState<number>(1000); // Example input amount
  const [calculatedResult, setCalculatedResult] = useState<number>(1500); // Example calculated result

  const inputDate = '01/2022'; // Example input date in MM/YYYY format
  const todayDate = new Date();
  const endDate = `${todayDate.getMonth() + 1 < 10 ? '0' : ''}${todayDate.getMonth() + 1}/${todayDate.getFullYear()}`;

  const dateRange = generateDateRange(inputDate, endDate);

  const chartData = {
    labels: dateRange,
    datasets: [
      {
        label: 'Investment Return',
        data: [inputAmount, calculatedResult],
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(255, 255, 255, 1)', // Changed to white
        fill: true,
      },
    ],
  };

  return (
    <div>
      <Line data={chartData} />
    </div>
  );
};

export default InvestmentChart;
