import React, { useState, useEffect } from 'react';
import { getStockPricesInRange } from './RealStockData';
import 'chartjs-adapter-date-fns';
import { Chart, ChartOptions, TimeScale, LineController, LineElement, PointElement, LinearScale, Title } from 'chart.js';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';

interface InvestmentChartProps {
  startDate: string;
  initialInvestment: number;
  finalValue: number;
}

const InvestmentChart: React.FC<InvestmentChartProps> = ({ startDate, initialInvestment }) => {
  const [chartData, setChartData] = useState<any>({});

  useEffect(() => {
    const stockData = getStockPricesInRange(startDate, new Date().toISOString().slice(0, 10));

    const dates = stockData.map(data => data.date);
    const prices = stockData.map(data => data.price);

    const investmentValues = prices.map(price => (initialInvestment / prices[0]) * price);

    setChartData({
      labels: dates,
      datasets: [
        {
          label: 'Investment Value Over Time',
          data: investmentValues,
          borderColor: 'rgba(75, 192, 192, 1)',
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          fill: true,
        },
      ],
    });
  }, [startDate, initialInvestment]);

  const options = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'month',
          tooltipFormat: 'MM/YYYY',
          displayFormats: {
            month: 'MMM YYYY',
          },
        },
      },
      y: {
        beginAtZero: true,
      },
    },
  } as ChartOptions<'line'>;

  return (
    <div style={{ backgroundColor: 'white', padding: '1rem' }}>
      {chartData.labels && chartData.datasets ? (
        <Line data={chartData} options={options} />
      ) : (
        <p>Loading chart...</p>
      )}
    </div>
  );
};

export default InvestmentChart;
