// src/services/salesforce.ts
import axios from 'axios';
import { Todo } from '../types';

const INSTANCE_URL = 'https://SWE42.salesforce.com'; // Replace with your Salesforce instance URL
const API_BASE_URL = `${INSTANCE_URL}/services/apexrest/api/public`;
const SESSION_ID = 'YOUR_SESSION_ID'; // Replace with your session ID

axios.defaults.headers.common['Authorization'] = `Bearer ${SESSION_ID}`;
axios.defaults.headers.common['Content-Type'] = 'application/json';

export const getTodos = async (): Promise<Todo[]> => {
  const response = await axios.get(`${API_BASE_URL}/todos`);
  const data = response.data;
  return data.map((item: any) => ({
    id: item.Id,
    title: item.Title__c,
    completed: item.Completed__c,
  }));
};

export const addTodo = async (todo: Omit<Todo, 'id'>): Promise<Todo> => {
  const response = await axios.post(`${API_BASE_URL}/todos`, {
    Title__c: todo.title,
    Completed__c: todo.completed,
  });
  const item = response.data;
  return {
    id: item.Id,
    title: item.Title__c,
    completed: item.Completed__c,
  };
};

export const updateTodo = async (todo: Todo): Promise<void> => {
  await axios.patch(`${API_BASE_URL}/todos/${todo.id}`, {
    Title__c: todo.title,
    Completed__c: todo.completed,
  });
};

export const deleteTodo = async (id: number): Promise<void> => {
  await axios.delete(`${API_BASE_URL}/todos/${id}`);
};
