// src/App.tsx
import React, { useState } from 'react';
import { Todo } from './types';

function App() {
  // State to manage the list of to-do items, 
  // the current input values for the title, 
  // the next unique identifier for new to-do items.
  const [todos, setTodos] = useState<Todo[]>([]);
  const [title, setTitle] = useState('');
  const [nextId, setNextId] = useState(1);

  // Function to handle adding a new to-do item.
  const handleAddTodo = () => {
    if (title.trim() === '') return;
    const newTodo: Todo = { id: nextId, title, completed: false };
    setTodos([...todos, newTodo]);
    setTitle('');
    setNextId(nextId + 1);
  };

  // Update
  const handleUpdateTodo = (id: number, updatedTitle: string) => {
    setTodos(
      todos.map((todo) => (todo.id === id ? { ...todo, title: updatedTitle } : todo))
    );
  };

  // Toggle
  const handleToggleCompleted = (id: number) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };
  // Delete
  const handleDeleteTodo = (id: number) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-md mx-auto bg-white shadow-md rounded p-6">
        <h1 className="text-2xl font-bold mb-4">To-Do App</h1>
        <div className="flex mb-4">
          <input
            type="text"
            className="flex-grow border rounded p-2"
            placeholder="Add a new to-do"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <button
            onClick={handleAddTodo}
            className="ml-2 bg-blue-500 text-white px-4 py-2 rounded"
          >
            Add
          </button>
        </div>
        <ul>
          {todos.map((todo) => (
            <li
              key={todo.id}
              className="flex items-center justify-between mb-2 p-2 border-b"
            >
              <div className="flex items-center">
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => handleToggleCompleted(todo.id)}
                  className="mr-2"
                />
                <input
                  type="text"
                  value={todo.title}
                  onChange={(e) => handleUpdateTodo(todo.id, e.target.value)}
                  className={`border-b focus:outline-none ${
                    todo.completed ? 'line-through text-gray-500' : ''
                  }`}
                />
              </div>
              <button
                onClick={() => handleDeleteTodo(todo.id)}
                className="text-red-500"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
